mapper.select:
		SELECT 
		FROM xxl_apm_heartbeat_report AS t
		WHERE t.appname = #{appname}
			AND t.addtime between #{addtime_from} and #{addtime_to}
            AND t.ip = #{ip}
		ORDER BY t.addtime ASC

SELECT distinct t.ip, t.hostname
FROM xxl_apm_heartbeat_report AS t
WHERE t.appname = #{appname}
  AND t.addtime between #{addtime_from} and #{addtime_to}
ORDER BY t.ip ASC
